app.factory('beerService', function(){
  var beers = [
    {
      name: "duvel",
      style: " Belgian Strong Pale Ale ",
      abv: 8.5,
      image_url :"data:image/jpeg;base64,",
      ratings:5,
      numRate:1,
    },
    {
      name: "hobgoblin",
      style: " Extra Special / Strong Bitter (ESB)",
      abv: 5.20,
      image_url : "data:image/jpeg;base64,",
      ratings:12,
      numRate:3,
      },
    {
      name: "Guinness",
      style: " Irish Dry Stout ",
      abv: 6,
      image_url :"http://wp.patheos.com.s3.amazonaws.com/blogs/bristolpalin/files/2014/07/guinness1-1024x768.jpg",
      ratings:1,
      numRate:1
    },
    {
      name: "Goldstar unfiltered",
      style: " Kellerbier / Zwickelbier ",
      abv: 4.90,
      image_url :"https://mir-s3-cdn-cf.behance.net/project_modules/disp/bda79e11197783.560f39e2f2adc.jpg",
      ratings:0,
      numRate:0
    }
  ];
  var getBeer = function(name){
    for (var i = 0;i< beers.length;i++){
      if (beers[i].name===name) {
        return beers[i];
      }//if
    }//for
  };//getBeer
  var addBeer = function(beer) {
    beers[beers.length] = beer;
  };
  var remove = function(index) {
        beers.splice(index,1);
  }//remove
var updateAvg = function(rating, beer){
    beer.ratings+=rating;
      beer.numRate++;
};//updateAvg
var getAvg = function(beer) {
  if (beer.numRate!==0) {
    beer.currAvg= (beer.ratings/beer.numRate);
  } else {
    beer.currAvg =("no ratings yet for this beer!");

  }
  return beer.currAvg;
}//getAvg
  return {
    beers: beers,
    remove: remove,
    addBeer: addBeer,
    getBeer: getBeer,
    getAvg: getAvg,
    updateAvg: updateAvg
  }
})
